(548, 'sticky-header-2020-scripts', '/* Script to toggle the custom sticky header class on scroll */let stk_scroll_pos = 0;let stk_ticking = false;let eventMin = new CustomEvent( \'stickyHeaderMinified\',{done:true});let eventMax = new CustomEvent( \'stickyHeaderMaxified\',{done:true});function toggleLogoClass( scroll_pos ){setTimeout( function (){let sel = document.getElementById( \'site-header\' );if ( sel ){/* Do something with the scroll position. */if ( scroll_pos>84 ){if ( sel.classList.contains( \'sticky-header-minified\' ) ){/* Nothing to do,the class was added previously. */}else{sel.classList.add( \'sticky-header-minified\' );document.getElementsByTagName( \'body\' )[0].classList.add(\'with-sticky-header-minified\');window.dispatchEvent( eventMin );}}else{if ( ! sel.classList.contains( \'sticky-header-minified\' ) ){/* Nothing to do,the class was removed previously. */}else{sel.classList.remove( \'sticky-header-minified\' );document.getElementsByTagName( \'body\' )[0].classList.remove(\'with-sticky-header-minified\');window.dispatchEvent( eventMax );}}}},200 );}window.addEventListener( \'scroll\',function ( e ){stk_scroll_pos = window.scrollY;if ( ! stk_ticking ){window.requestAnimationFrame( function (){toggleLogoClass( stk_scroll_pos );stk_ticking = false;});stk_ticking = true;}});/* Listen for scroll to top click */const sh2020A = document.querySelectorAll( \'a[href="#site-header"]\' );if ( sh2020A ){sh2020A.forEach( item =>{item.addEventListener( \'click\',event =>{document.body.scrollTop = 0;document.documentElement.scrollTop = 0;});});}', 'yes'),
